// Accepts an object followed by any number of keys of the object.
export const pickKeys = <T, K extends keyof T = keyof T>(obj: T, ...keys: K[]): Pick<T, K> => {
    // Returns an object produced from mapping over each key.
        return keys.reduce((acc, key) => {
        // Checks if the current key is present in the object.
                if (hasKey(obj, key)) {
             // Sets that key in the accumulator to its value in the object if it is present.
                        acc[key] = obj[key];
                }
        // Returns the accumulator.
                return acc;
    // Provides an object as the base case for the mapping, asserting that its type is a subset of T whose keys are only the members of K.
        }, {} as Pick<T, K>);
};

